package Pack1;
import java.util.Scanner;

public class printnumberwhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner scan=new Scanner(System.in);
		System.out.println("enter number:");
		int N=scan.nextInt();
		
		while(N>0) {
			System.out.println("enter number:");
			N=scan.nextInt();
			
		}
		

	}

}
